import React,{useState} from 'react'
import './OCMain.css'
export default function OCMain(){
    const [activeTab, setActiveTab] = useState("winner");

    let data = [
        {
            "category": "winner",
            "title":"Recent Winner",
            "description":"John Doe, dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
            "imgUrl":"https://unsplash.com/photos/yd4ubMUNTG0"

        }
    ]
    const filtered = data.filter(item => item.category.length > 4)
    console.log(filtered);

    return(
        <div className="container">
            <nav>
                <ul className="block__tabs">
                    <li className={activeTab === "winner"? "active" : ""} onClick={() => setActiveTab("winner")}>Recent Winners</li>
                    <li className={activeTab === "newGiveaways" ? "active" : ""} onClick={() => setActiveTab("newGiveaways")}>New Giveaways</li>
                    <li className={activeTab === "endingGiveaways" ? "active" : ""} onClick={() => setActiveTab("endingGiveaways")}>Ending Giveaways</li>
                </ul>
            </nav>
            <div className="content__info">
                {data.filter((item) => item.category === activeTab).map((tabbedContent) => {return(<div className="content__info">
                <img className="tabbedContent__image" src={tabbedContent.imgUrl} alt=""/>
                <h1 className="tabbedContent__header" >  {tabbedContent.title}</h1>
                <p className="tabbedContent__descript" >hello {tabbedContent.description}</p>
            </div>)})}
            </div>
        </div>
    )
}
