import React from 'react'
import Header from './Components/Header/Header'
import Main from './Components/Main/Main'
import './App.css';

function App() {

  const onScroll = (e) => {
    const currentScrollY = e.target.scrollTop;
    console.log(currentScrollY);
  }
  return (
    <div onScroll={onScroll} className="App">
      <header>
        <Header/>
      </header>
      <main>
      <Main/>
      </main>
      <button className="scroll__button">&#8743;</button>
    </div>
  );
}

export default App;
